import base64
from PIL import Image
from io import BytesIO

# La cadena codificada en base64
data = "data:image/jpeg;base64,"

# Separa la parte de datos base64 de la cadena
data = data.split(',')[1]

# Decodifica la cadena base64
image_data = base64.b64decode(data)

# Abre la imagen utilizando la biblioteca PIL (Python Imaging Library)
image = Image.open(BytesIO(image_data))

# Guarda la imagen en un archivo (por ejemplo, 'imagen.jpg')
image.save('imagen.jpg')

# Cierra la imagen
image.close()
